package rancher2

import (
	"fmt"
	"testing"

	"github.com/hashicorp/terraform-plugin-sdk/helper/resource"
	"github.com/hashicorp/terraform-plugin-sdk/terraform"
)

const (
	testAccRancher2CertificateType = "rancher2_certificate"
)

var (
	testAccRancher2Certificate               string
	testAccRancher2CertificateUpdate         string
	testAccRancher2CertificateConfig         string
	testAccRancher2CertificateUpdateConfig   string
	testAccRancher2CertificateNs             string
	testAccRancher2CertificateNsUpdate       string
	testAccRancher2CertificateNsConfig       string
	testAccRancher2CertificateNsUpdateConfig string
)

func init() {
	testAccRancher2Certificate = `
resource "` + testAccRancher2CertificateType + `" "foo" {
  name = "foo"
  description = "Terraform certificate acceptance test"
  project_id = rancher2_cluster_sync.testacc.default_project_id
  key = ""
  certs = ""
}
`
	testAccRancher2CertificateUpdate = `
resource "` + testAccRancher2CertificateType + `" "foo" {
  name = "foo"
  description = "Terraform certificate acceptance test - updated"
  project_id = rancher2_cluster_sync.testacc.default_project_id
  key = ""
  certs = ""
}
`
	testAccRancher2CertificateConfig = testAccCheckRancher2ClusterSyncTestacc + testAccRancher2Certificate
	testAccRancher2CertificateUpdateConfig = testAccCheckRancher2ClusterSyncTestacc + testAccRancher2CertificateUpdate

	testAccRancher2CertificateNs = `
resource "` + testAccRancher2CertificateType + `" "foo-ns" {
  name = "foo-ns"
  description = "Terraform certificate acceptance test"
  project_id = rancher2_cluster_sync.testacc.default_project_id
  namespace_id = rancher2_namespace.testacc.id
  key = ""
  certs = ""
}
`
	testAccRancher2CertificateNsUpdate = `
resource "rancher2_certificate" "foo-ns" {
  name = "foo-ns"
  description = "Terraform certificate acceptance test - updated"
  project_id = rancher2_cluster_sync.testacc.default_project_id
  namespace_id = rancher2_namespace.testacc.id
  key = ""
  certs = ""
}
 `
	testAccRancher2CertificateNsConfig = testAccCheckRancher2ClusterSyncTestacc + testAccCheckRancher2NamespaceTestacc + testAccRancher2CertificateNs
	testAccRancher2CertificateNsUpdateConfig = testAccCheckRancher2ClusterSyncTestacc + testAccCheckRancher2NamespaceTestacc + testAccRancher2CertificateNsUpdate

}

func TestAccRancher2Certificate_basic_Project(t *testing.T) {
	var reg interface{}

	resource.Test(t, resource.TestCase{
		Providers:    testAccProviders,
		CheckDestroy: testAccCheckRancher2CertificateDestroy,
		Steps: []resource.TestStep{
			{
				Config: testAccRancher2CertificateConfig,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckRancher2CertificateExists(testAccRancher2CertificateType+".foo", reg),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "name", "foo"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "description", "Terraform certificate acceptance test"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "certs", ""),
				),
			},
			{
				Config: testAccRancher2CertificateUpdateConfig,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckRancher2CertificateExists(testAccRancher2CertificateType+".foo", reg),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "name", "foo"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "description", "Terraform certificate acceptance test - updated"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "certs", ""),
				),
			},
			{
				Config: testAccRancher2CertificateConfig,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckRancher2CertificateExists(testAccRancher2CertificateType+".foo", reg),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "name", "foo"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "description", "Terraform certificate acceptance test"),
					resource.TestCheckResourceAttr(testAccRancher2CertificateType+".foo", "certs", ""),
				),
			},
		},
	})
}

func TestAccRancher2Certificate_disappears_Project(t *testing.T) {
	var reg interface{}

	resource.Test(t, resource.TestCase{
		Providers:    testAccProviders,
		CheckDestroy: testAccCheckRancher2CertificateDestroy,
		Steps: []resource.TestStep{
			{
				Config: testAccRancher2CertificateConfig,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckRancher2CertificateExists(testAccRancher2CertificateType+".foo", reg),
					testAccRancher2CertificateDisappears(reg),
				),
				ExpectNonEmptyPlan: true,
			},
		},
	})
}

